// CategoryGamesComponent.js
import React from 'react';
import GameCardComponent from './GameCardComponent';

const CategoryGamesComponent = ({ games }) => {
    if (!Array.isArray(games)) {
        return <p>No games available</p>;
    }

    return (
        <div>
            <h2>Jeux les plus joués par catégorie</h2>
            <div>
                {games.map((game, index) => (
                    <GameCardComponent key={index} game={game} />
                ))}
            </div>
        </div>
    );
};

export default CategoryGamesComponent;
