// Dashboard.js
import React, { useState, useEffect } from 'react';
import CategoryGamesComponent from './CategoryGamesComponent';
import CountryGamesComponent from './CountryGamesComponent';
import axios from 'axios';

const Dashboard = () => {
    const [categoryGames, setCategoryGames] = useState([]);
    const [countryGames, setCountryGames] = useState([]);

    useEffect(() => {
        // Fonction pour charger les jeux les plus joués par catégorie
        const fetchCategoryGames = async () => {
            try {
                const response = await axios.get('API_URL/categorygames');
                setCategoryGames(response.data);
            } catch (error) {
                console.error('Error fetching category games:', error);
            }
        };

        // Fonction pour charger les jeux les plus joués par pays
        const fetchCountryGames = async () => {
            try {
                const response = await axios.get('API_URL/countrygames');
                setCountryGames(response.data);
            } catch (error) {
                console.error('Error fetching country games:', error);
            }
        };

        fetchCategoryGames();
        fetchCountryGames();
    }, []);

    return (
        <div>
            <h1>Steam Dashboard</h1>
            <CategoryGamesComponent games={categoryGames} />
            <CountryGamesComponent games={countryGames} />
        </div>
    );
};

export default Dashboard;
