// Importer Axios dans votre fichier
import axios from 'axios';

// URL de base de l'API SteamSpy
const BASE_URL = 'https://steamspy.com/api.php';

// Fonction pour récupérer les jeux les plus joués par catégorie
const getTopGamesByCategory = async (category) => {
    try {
        // Effectuer une requête GET à l'API SteamSpy avec la catégorie spécifiée
        const response = await axios.get(`${BASE_URL}?request=top100in${category}`);
        
        // Renvoyer les données de réponse
        return response.data;
    } catch (error) {
        // Gérer les erreurs
        console.error('Erreur lors de la récupération des jeux par catégorie :', error);
        return null;
    }
};

// Exemple d'utilisation de la fonction
const category = 'action'; // Catégorie de jeux, par exemple 'action'
getTopGamesByCategory(category)
    .then(data => {
        // Traiter les données récupérées ici
        console.log('Jeux les plus joués dans la catégorie', category, ':', data);
    })
    .catch(error => {
        // Gérer les erreurs
        console.error('Erreur lors de la récupération des jeux par catégorie :', error);
    });
